import React, {useEffect, useState} from 'react';

import BackendErrorMessages from 'components/BackendErrorMessages';

const ArticleForm = ({onSubmit, errors, initialValues = null}) => {
    const [title, setTitle] = useState('');
    const [body, setBody] = useState('');
    const [description, setDescription] = useState('');
    const [tagList, setTagList] = useState([]);
    const [tag, setTag] = useState('');

    const handleSubmit = event => {
        event.preventDefault();
        const article = {
            title,
            body,
            description,
            tagList
        }
        onSubmit(article)
    }

    const addTagInTagList = event => {
        if (event.code !== 'Enter')
            return
        event.preventDefault()
        setTagList([...tagList, tag]);
        setTag('');
    }

    const deleteTagInTagList = index => {
        setTagList([...tagList.slice(0, index), ...tagList.slice(index + 1)])
    }

    useEffect(() => {
        if (!initialValues)
            return

        setTitle(initialValues.title)
        setBody(initialValues.body)
        setDescription(initialValues.description)
        setTagList(initialValues.tagList)
    }, [initialValues])

    return (
        <div className='editor-page'>
            <div className='container page'>
                <div className='row'>
                    <div className='col-md-10 offset-md-1 col-xs-12'>
                        {errors && <BackendErrorMessages backendErrors={errors}/>}
                        <form onSubmit={handleSubmit}>
                            <fieldset className='form-group'>
                                <input
                                    type="text"
                                    className='form-control form-control-lg'
                                    placeholder='Article title'
                                    value={title}
                                    onChange={e => setTitle(e.target.value)}
                                />
                            </fieldset>
                            <fieldset className='form-group'>
                                <input
                                    type="text"
                                    className='form-control form-control-lg'
                                    placeholder='What is this article about?'
                                    value={description}
                                    onChange={e => setDescription(e.target.value)}
                                />
                            </fieldset>
                            <fieldset className='form-group'>
                                <textarea
                                    className='form-control'
                                    rows='8'
                                    placeholder='Write tour article (in markdown)'
                                    value={body}
                                    onChange={e => setBody(e.target.value)}
                                >
                                </textarea>
                            </fieldset>
                            <fieldset className='form-group'>
                                <input
                                    type="text"
                                    className='form-control form-control-lg'
                                    placeholder='Enter tags'
                                    value={tag}
                                    onChange={e => setTag(e.target.value)}
                                    onKeyDown={addTagInTagList}
                                />
                            </fieldset>
                            <div className='tag-list'>
                                {tagList.map((item, index) => (
                                    <span className='tag-default tag-pill' key={index}>
                                        <i className='ion-close-round' onClick={() => deleteTagInTagList(index)}></i>
                                        {item}
                                    </span>
                                ))}
                            </div>
                            <fieldset className='form-group'>
                                <button
                                    type='Submit'
                                    className='btn btn-lg pull-xs-right btn-primary'
                                >
                                    Publish Article
                                </button>
                            </fieldset>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ArticleForm;