import React, {useEffect, useState} from "react";
import classNames from "classnames";
import useFetch from "hooks/useFetch";

const SubscribeUser = ({userName, following}) => {
    const api = `/profiles/${userName}/follow`;
    const [{response, isLoading}, doFetch] = useFetch(api);

    const [isFollowind, setIsFollowind] = useState(following)

    console.log('response', response)

    const handleClick = () => {
        if (isLoading)
            return

        doFetch({
            method: isFollowind ? 'DELETE' : 'POST'
        })
    }

    useEffect(() => {
        if (!response) return

        setIsFollowind(response.profile.following);
    }, [response]);

    const classNameButton = classNames('btn', 'btn-sm', 'action-btn', 'btn-outline-secondary', {'disabled': isLoading})

    return (
        <span>
            <button className={classNameButton} onClick={handleClick}>
                <i className='ion-plus-round'></i>
                {`\t${isFollowind ? 'Unfollow' : 'Follow'} ${userName}`}
            </button>
        </span>
    )
}

export default SubscribeUser;